import { type ClassValue, clsx } from "clsx";
import { twMerge } from "tailwind-merge";

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs));
}

//Format currency IDR
export function formatCurrency(amount: string) {
  return new Intl.NumberFormat("id-ID", {
    style: "currency",
    currency: "IDR",
  }).format(parseInt(amount));
}

export const placeholderBlurhash =
  "data:image/png;base64,";

export const budgetEstimate = (budget: string) => {
  switch (budget) {
    case "low":
      return "around 0-3.000.000 IDR";
    case "medium":
      return "around 3.000.000-5.000.000 IDR";
    case "high":
      return "around 5.000.000+++ IDR";
  }
};

export const statusLifeQuality = (index: number) => {
  if (index >= 0 && index <= 2.5) {
    return "Very Low";
  } else if (index > 2.5 && index <= 3.5) {
    return "Low";
  } else if (index > 3.5 && index <= 4.5) {
    return "Medium";
  } else if (index > 4.5 && index <= 5) {
    return "High";
  }
};

//convert HexToRGB
export function hexToRgb(hex: string) {
  var shorthandRegex = /^#?([a-f\d])([a-f\d])([a-f\d])$/i;
  hex = hex.replace(shorthandRegex, function (m, r, g, b) {
    return r + r + g + g + b + b;
  });

  var result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
  return result
    ? {
        r: parseInt(result[1], 16),
        g: parseInt(result[2], 16),
        b: parseInt(result[3], 16),
      }
    : null;
}

export function genRandomNumbers(min: number, max: number, count: number) {
  const arr = [];
  while (arr.length < count) {
    const r = Math.floor(Math.random() * (max - min)) + min;
    if (arr.indexOf(r) === -1) arr.push(r);
  }

  return arr;
}
